import React from "react";
import "./style.css";
import axios from "axios";
const url = `https://api.github.com/users`;
export default function App() {
  let [s, setS] = React.useState("");
  let [myresults, setMyresults] = React.useState([]);
  React.useEffect(function(){
    let b = []
    if (localStorage.getItem("results")) {
      b = JSON.parse(localStorage.getItem("results"));

    }


    setMyresults(b);

  },[])
  // const abc = val => {
  //   let a = JSON.parse(localStorage.getItem("mysearch"));

  //   a.push(val);
  //   localStorage.setItem("mysearch", JSON.stringify(a));
  //   setSearch(val);
  // };

  const _search = e => {
    axios
      .get(url + "/" + s)
      .then(d => d.data)
      .then(d => {
        if (d) {
          let b = []
          if (localStorage.getItem("results")) {
            b = JSON.parse(localStorage.getItem("results"));

          }
          b.push(d);
          localStorage.setItem("results", JSON.stringify(b));
          setMyresults(b);
        }
        else{
          alert("not found")
        }
      });
  };
  const del = index => {
    let b = []
    if (localStorage.getItem("results")) {
      b = JSON.parse(localStorage.getItem("results"));

    }
    b.splice(index, 1)
    localStorage.setItem("results", JSON.stringify(b));
    setMyresults(b);
  }
  return (
    <div className="body">
      <h1>Github Search</h1>

      <input
      className="input"
        onChange={e => setS(e.target.value)}
        placeholder="search..."
        value={s}
      />
      <button className="button" onClick={_search}>search </button>
      <div className="pre">
        {myresults.map((x, i) => (
          <li key={i}>
            {x.id} {x.name}
            <button onClick={e => del(i)}>delete</button>
          </li>
        ))}
      </div>
    </div>
  );
}
