import React, { Fragment, Component } from "react";

import "./App.css";

import SearchBox from "./components/search-box/search-box.component";
import Card from "./components/card/card.component";

class App extends Component {
  userData;
  constructor(props) {
    super(props);
    this.state = {
      searchName: "",
      userData: "",
    };
  }

  onInputChange = (event) => {
    this.setState({ searchName: event.target.value }, () => {
      this.getData(this.state.searchName);

    });
  };

  getData = (userName) => {
    fetch(`https://api.github.com/users/${userName}`)
      .then((res) => res.json())
      .then((data) => this.setState({ userData: data }))
      .catch((error) => {
        alert("Oops! Could not reach GitHub");
        console.log("Oops! We have an error", error);
      });
  };
  componentDidMount() {
    this.userData = JSON.parse(localStorage.getItem('user'));

    if (localStorage.setItem('user')) {
        this.setState({
            searchName: this.userData.name,

        })
    } else {
        this.setState({
            searchName:'',
        })
    }
}

componentWillUpdate(nextProps, nextState) {
    localStorage.setItem('user', JSON.stringify(nextState));
}



  render() {
    return (
      <Fragment>
        <div className="heading">Github User Info</div>


        <SearchBox onInputChange={this.onInputChange} />
        {this.state.userData.id && this.state.searchName !== "" ? (
          <Card profile={this.state.userData} />
        ) : (
          <div className="no-data">
            No user Found! <br />
          </div>
        )}
      </Fragment>
    );
  }
}

export default App;
