import React from "react";
import "./style.css";
const url = `https://api.github.com/users`;
import axios from "axios";
export default function App() {
  let [s, setSearch] = React.useState("");
  let [myresults, setMyresults] = React.useState([]);
  const abc = val => {
    let a = JSON.parse(localStorage.getItem("mysearch"));

    a.push(val);
    localStorage.setItem("mysearch", JSON.stringify(a));
    setSearch(val);
  };

  const _search = e => {
    axios
      .get(url + "/" + s)
      .then(d => d.data)
      .then(d => {
        let b = JSON.parse(localStorage.getItem("results"));
        b.push(d);
        localStorage.setItem("results", JSON.stringify(b));
        setMyresults(b);
      });
  };
  return (
    <div>
      <h1>Search</h1>
      <p>Start editing to see some magic happen :)</p>
      <input
        onChange={e => abc(e.target.value)}
        placeholder="search..."
        value={s}
      />
      <button onClick={_search}>search </button>
      <div className="pre">
        {myresults.map((x,i) => (
          <li>
            {x.id} {x.name}
            <button onClick={e=>del(i)}>delete</button>
          </li>
        ))}
      </div>
    </div>
  );
}
